  if (item.binary) {
    if (!newItem.binary.video) {
      if (item.binary.data) newItem.binary.video = item.binary.data;
      else {
        const keys = Object.keys(item.binary);
        if (keys.length) newItem.binary.video = item.binary[keys[0]];
      }
    }
  }

  out.push(newItem);
}

return out;

