// Function node: parse AI JSON output and normalize root fields
const out = [];

for (const item of items) {
  const newItem = {
    json: { ...item.json },
    binary: item.binary ? { ...item.binary } : {}
  };

  // Handle AI output variations
  const raw = item.json?.output ?? item.json?.result ?? item.json?.content ?? null;

  let parsed = null;
  if (raw) {
    if (typeof raw === 'string') {
      try { 
        parsed = JSON.parse(raw.trim()); 
      } catch (e) {
        const match = raw.match(/\{[\s\S]*\}/);
        if (match) {
          try { parsed = JSON.parse(match[0]); } 
          catch (e2) { parsed = null; }
        }
      }
    } else if (typeof raw === 'object') {
      parsed = raw;
    }
  }

  if (parsed) {
    // YouTube fields
    newItem.json.youtubeTitle = parsed.youtubeTitle ?? '';
    newItem.json.youtubeDescription = parsed.youtubeDescription ?? '';

    // --- Tag normalization section (final version with character-safe builder) ---
    let tags = parsed.youtubeTags ?? parsed.youtube_tags ?? parsed.tags ?? [];

    if (typeof tags === 'string') {
      tags = tags.split(/[,|\n]/).map(s => s.trim()).filter(Boolean);
    }

    if (Array.isArray(tags)) {
      const cleanTags = [];
      let totalChars = 0;

      for (let t of tags) {
        // Sanitize and clean
        t = t.replace(/[#|@'"\[\]{}]/g, '').trim();
        if (t.length < 2) continue;

        // +2 for ", " separator
        const addedLength = (cleanTags.length > 0 ? 2 : 0) + t.length;

        // Stop before exceeding YouTube's 500-char limit
        if (totalChars + addedLength > 499) break;

        cleanTags.push(t);
        totalChars += addedLength;
      }

      // Join and finalize
      newItem.json.youtubeTags = cleanTags.join(', ');

      // Optional log for debugging
      console.log(`‚úÖ Final tag length: ${newItem.json.youtubeTags.length}`);
      console.log(`‚úÖ Total tags used: ${cleanTags.length}`);
    } else {
      newItem.json.youtubeTags = '';
    }

    // LinkedIn fields
    newItem.json.linkedinTitle = parsed.linkedinTitle ?? '';
    newItem.json.linkedinDescription = parsed.linkedinDescription ?? '';
    newItem.json.linkedinHashtags = parsed.linkedinHashtags ?? '';

    // Optional: track original video source
    newItem.json.inputVideoUrl = parsed.inputVideoUrl ?? newItem.json.videoUrl ?? '';

    // ‚úÖ Add metadata validation flag (for IF node)
    const isValid =
      newItem.json.youtubeTitle &&
      newItem.json.youtubeTags &&
      newItem.json.youtubeTitle.trim() !== '' &&
      newItem.json.youtubeTags.trim() !== '';

    newItem.json.metadataValid = isValid;
  } else {
    newItem.json.ai_raw_output = raw;
    newItem.json.metadataValid = false;
  }

  out.push(newItem);
}

return out;

// Function node: Construct final YouTube URL from Upload ID
const out = [];
const BASE_URL = 'https://youtu.be/';

for (const item of items) {
  const newItem = {
    json: { ...item.json },
    binary: item.binary ? { ...item.binary } : {}
  };

  // The YouTube Upload node typically outputs the ID in the 'id' field
  const uploadId = item.json?.id ?? item.json?.uploadId ?? null;

  if (uploadId) {
    // Construct the full URL
    newItem.json.finalYoutubeUrl = BASE_URL + uploadId;
  } else {
    // If ID is missing, set an empty string (or handle error)
    newItem.json.finalYoutubeUrl = '';
  }

  out.push(newItem);
}

return out;

// Function node: clean AI output for direct YouTube publishing (no spaces in joined tags)
const out = [];

for (const item of items) {
  const newItem = {
    json: {},
    binary: item.binary ? { ...item.binary } : {}
  };

  const raw = item.json?.output ?? item.json?.result ?? item.json?.content ?? null;
