  if (raw) {
    if (typeof raw === 'string') {
      try { parsed = JSON.parse(raw.trim()); } 
      catch {
        const match = raw.match(/\{[\s\S]*\}/);
        if (match) { try { parsed = JSON.parse(match[0]); } catch {} }
      }
    } else if (typeof raw === 'object') parsed = raw;
  }

  if (parsed) {
    // ‚úÖ Title & Description
    newItem.json.youtubeTitle = parsed.youtubeTitle?.trim() ?? '';
    newItem.json.youtubeDescription = parsed.youtubeDescription?.trim() ?? '';

    // ‚úÖ Handle Tags - no spaces between commas
    let tags = parsed.youtubeTags ?? parsed.youtube_tags ?? parsed.tags ?? [];
    if (typeof tags === 'string')
      tags = tags.split(/[,|\n]/).map(s => s.trim()).filter(Boolean);

    if (Array.isArray(tags)) {
      const cleanTags = [];
      let totalChars = 0;

      for (let t of tags) {
        t = t.replace(/[#|@'"\[\]{}]/g, '').trim();
        if (t.length < 2) continue;

        const addedLength = (cleanTags.length > 0 ? 1 : 0) + t.length; // 1 for comma only
        if (totalChars + addedLength > 499) break;

        cleanTags.push(t);
        totalChars += addedLength;
      }

      // ‚úÖ Join tags without spaces between commas
      newItem.json.youtubeTags = cleanTags.join(',');
    } else {
      newItem.json.youtubeTags = '';
    }

    // LinkedIn fields
    newItem.json.linkedinTitle = parsed.linkedinTitle?.trim() ?? '';
    newItem.json.linkedinDescription = parsed.linkedinDescription?.trim() ?? '';
    newItem.json.linkedinHashtags = parsed.linkedinHashtags?.trim() ?? '';

    // Optional: original source
    newItem.json.inputVideoUrl = parsed.inputVideoUrl ?? item.json.videoUrl ?? '';
  } else {
    // Default blank schema if parse fails
    newItem.json.youtubeTitle = '';
    newItem.json.youtubeDescription = '';
    newItem.json.youtubeTags = '';
    newItem.json.linkedinTitle = '';
    newItem.json.linkedinDescription = '';
    newItem.json.linkedinHashtags = '';
    newItem.json.inputVideoUrl = item.json.videoUrl ?? '';
  }

  out.push(newItem);
}

return out;

